import {Observer} from 'rxjs/src/internal/types';
import {config} from 'rxjs/src/internal/config';
import {hostReportError} from 'rxjs/src/internal/util/hostReportError';

export const empty: Observer<any> = {
    closed: true,
    next(value: any): void { /* noop */
    },
    error(err: any): void {
        if (config.useDeprecatedSynchronousErrorHandling) {
            throw err;
        } else {
            hostReportError(err);
        }
    },
    complete(): void { /*noop*/
    }
};
