import {Observable} from 'rxjs/src/internal/Observable';
import {SchedulerLike} from 'rxjs/src/internal/types';
import {subscribeToArray} from 'rxjs/src/internal/util/subscribeToArray';
import {scheduleArray} from 'rxjs/src/internal/scheduled/scheduleArray';

export function fromArray<T>(input: ArrayLike<T>, scheduler?: SchedulerLike) {
    if (!scheduler) {
        return new Observable<T>(subscribeToArray(input));
    } else {
        return scheduleArray(input, scheduler);
    }
}
