import {Observable} from 'rxjs/src/internal/Observable';
import {subscribeToObservable} from 'rxjs/src/internal/util/subscribeToObservable';
import {InteropObservable, SchedulerLike} from 'rxjs/src/internal/types';
import {scheduleObservable} from 'rxjs/src/internal/scheduled/scheduleObservable';

export function fromObservable<T>(input: InteropObservable<T>, scheduler?: SchedulerLike) {
    if (!scheduler) {
        return new Observable<T>(subscribeToObservable(input));
    } else {
        return scheduleObservable(input, scheduler);
    }
}
