import {SCHEMES} from "uri-js/src/uri";

import http from "uri-js/src/schemes/http";
import https from "uri-js/src/schemes/https";
import mailto from "uri-js/src/schemes/mailto";
import urn from "uri-js/src/schemes/urn";
import uuid from "uri-js/src/schemes/urn-uuid";

SCHEMES[http.scheme] = http;

SCHEMES[https.scheme] = https;

SCHEMES[mailto.scheme] = mailto;

SCHEMES[urn.scheme] = urn;

SCHEMES[uuid.scheme] = uuid;

export * from "uri-js/src/uri";
